import { GET_DOGS, FILTER, GET_BY_NAME, ADD_DOG, GET_TEMPERAMENTS, FILTER_WEIGHT, FILTER_NAME, DOG_DETAILS } from "../actions/constants";

let initialState = {
    dogs : [],
    allDogs : [],
    dogDetails : [],
    temperaments : []
};

const root = (state = initialState, action) => {
    switch (action.type) {
        case GET_DOGS :
        return {
            ...state,
            dogs: action.payload,
            allDogs : action.payload
        }
        case FILTER :
            const allDogs = state.allDogs;
            const filter = action.payload === 'todos' ? allDogs : allDogs.filter(e => {
                if(action.payload === 'agregada'){
                    return e.status 
                }
                if(action.payload === 'existente'){
                    return !e.status
                }
                else {
                    console.log(action.payload);
                    return e.temperament === action.payload
                }
            });
            return {
                ...state,
                dogs: filter
            };
        case GET_BY_NAME :
            const dogss = state.dogs;
            const search = dogss.filter(e => {
                if(e.name.toLowerCase().includes(action.payload.toLowerCase())){
                   return  e.name.toLowerCase().includes(action.payload.toLowerCase())
                }
            })          
            return {
                ...state,
                dogs: search
            };
        case ADD_DOG :
            return {
                ...state
            };
        case GET_TEMPERAMENTS : 
            return {
                ...state,
                temperaments: action.payload
            };     
        case FILTER_NAME :
            const dogs = state.dogs
            const filterName = action.payload === 'az' ?
                dogs.sort(function(a, b) {
                    if(a.name > b.name) {
                        console.log(a, b);
                        return 1;
                    }
                    if(b.name > a.name) {
                        return -1;
                    }
                    return 0;
                }) :
                dogs.sort(function(a, b) {
                    if(a.name > b.name) {
                        return -1;
                    }
                    if(b.name > a.name) {
                        return 1
                    }
                    return 0;
                });
                return {
                    ...state,
                    dogs: filterName
                } ;
            case FILTER_WEIGHT : 
                const dog = state.dogs;
                const filterWeight = action.payload === 'menor' ?
                dog.sort(function(a, b) {
                    if(a.weight.metric[0] > b.weight.metric[0]) {
                        return 1;
                    }
                    if(b.weight.metric[0] > a.weight.metric[0]) {
                        return -1;
                    }
                    return 0;
                }) :
                dog.sort(function(a, b) {
                    if(a.name > b.name) {
                        return -1;
                    }
                    if(b.name > a.name) {
                        return 1
                    }
                    return 0;
                });
            return {
                ...state,
                dogs: filterWeight
            } ;
        case DOG_DETAILS : 
        return{
            ...state,
            dogDetails : action.payload
        }
        default :
        return state;
    }
};

export default root;
