import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";

//import actions
import { addDog, getTemps } from '../../actions/actions';

//import components
import { Nav } from "../nav/nav";

//import css
import './add.css';

export const Add = () => {

    const [input, setInput] = useState({
        name : '',
        height_metric: '',
        height_imperial: '',
        weight_metric: '',
        weight_imperial: '',
        lifeSpan: '',
        image: '',
        temperament: []
    });

    const dispatch = useDispatch();
    const temperaments = useSelector((state) => state.temperaments);
    useEffect(() => {
        dispatch(getTemps());
    }, []);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
    };

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperament: [...input.temperament, e.target.value]
        })
    };

    const handleDelete = (e) => {
        e.preventDefault();
        input.temperament.filter(e => e !== e.id)
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(addDog(input));
        alert('Raza creada!');
        setInput({
            name : '',
            height_metric: '',
            height_imperial: '',
            weight_metric: '',
            weight_imperial: '',
            lifeSpan: '',
            image: '',
            temperament: []
        });
        <Redirect to = '/home' />
    }

    console.log(input);

    return(
        <>
            <Nav/>
                <div className = 'div_add_one'>
                    <h1 className = 'title_add'>CREAR RAZA</h1>
                        <form onSubmit = {e => handleSubmit(e)} className = 'form_add'>
                            <div className = 'div_add_name'>
                                <input type="text" value = {input.name} name = 'name' onChange = {e => handleChange(e)} placeholder = 'Nombre' className = 'input_add_name'/>
                            </div>
                            <div className = 'div_add_height'>
                                <label> Métrica de altura :</label>
                                <input type="number" value = {input.height_metric} name = 'height_metric' onChange = {e => handleChange(e)} className = 'input_add_height_metric'/>
                                <label> Altura imperial :</label>
                                <input type="number" value = {input.height_imperial} name="height_imperial" onChange = {e => handleChange(e)} className = 'input_add_height_imperial' />
                            </div>
                            <div>
                                <label> Métrica de peso :</label>
                                <input type="number" value = {input.weight_metric} name = 'weight_metric' onChange = {e => handleChange(e)} className = 'input_add_weight_metric' />
                                <label> Peso imperial :</label>
                                <input type="number" value = {input.weight_imperial} name="weight_imperial" onChange = {e => handleChange(e)} className = 'input_add_weight_imperial' />
                            </div>
                            <div>
                                <label> Esperanza de vida :</label>
                                <input type="number" value = {input.life_span} name="lifeSpan" onChange = {e => handleChange(e)} className = 'input_add_lifeSpan' />
                            </div>
                            <div>
                                <input type="text" value = {input.image} name="image" onChange = {e => handleChange(e)} className = 'input_add_img' placeholder = 'URL Imagen' />
                            </div>
                            <div>
                                <select onChange = {e => handleSelect(e)} className = 'select_add_temps'>
                                    <option value="temperaments">Temperamentos</option>
                                    {temperaments.map(e => {
                                        return <option value={e.name}>{e.name}</option>
                                    })}
                                </select>
                                <ul>
                                    {input.temperament.map(e => {
                                        return <li>{e}</li>
                                    })}
                                </ul>
                            </div>
                            <div>
                                <button className = 'buton_add_dog' type = 'submit'>Agregar </button>
                            </div>
                        </form>
            </div>
        </>
    )
}
