import axios from 'axios';

//import constants
import {GET_DOGS, FILTER, GET_BY_NAME} from './constants'

export const getDog = () => {
    return async function (dispatch){
        const json = await axios.get('http://localhost:3001/dogs');
        return dispatch({
            type: GET_DOGS,
            payload: json.data
        })
    }
};

export const filter = (payload) => {
    return {
        type: FILTER,
        payload
    }
};

export const getName = (payload) => {
    return {
        type: GET_BY_NAME,
        payload
    }
}
