import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

//import actions
import { addDog, getTemps } from '../../actions/actions';

//import components
import { Nav } from "../nav/nav";

//import css
import './add.css';

const validate = (input)  => {
    let err = {};
    if(!input.name) err.name = 'Se requiere un nombre';
    if(!input.height_max || !input.height_min) err.height = 'Se requiere una altura' ;
    if(!input.weight_max || !input.weight_min) err.weight = 'Se requiere un peso';
    if(!input.lifeSpan) err.lifeSpan = 'Se requiere un largo de vida';

    return err
};

export const Add = () => {

    const[name, setName] = useState('');
    const[error, setError] = useState('');

    const [input, setInput] = useState({
        name : '',
        height_min: '',
        height_max: '',
        weight_min: '',
        weight_max: '',
        lifeSpan: '',
        image: '',
        temperament: []
    });


    const validateName = (value) => {
        let err = '';
        if(!/^[a-zA-ZÀ-ÿ\s]{1,40}$/.test(value)){
            err = 'El nombre solo puede contener letras'
        }else {
            err='';
        }
        setInput({
            ...input,
            name: value
        })
    };

    const validateHeigth= (value) => {
        let err = '';
        if(!/^[0-9]$/.test(value)){
            err = 'El nombre solo puede contener letras'
        }else {
            err='';
        }
        setInput({
            ...input,
            name: value
        })
    };


    const dispatch = useDispatch();
    const temperaments = useSelector((state) => state.temperaments);
    useEffect(() => {
        dispatch(getTemps());
    }, [dispatch]);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        });
    };

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperament: [...input.temperament, e.target.value]
        })
    };

    const handleDelete = (el) => {
        setInput({
            ...input,
            temperament: input.temperament.filter( e => e !== el)
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(addDog(input));
        alert('Raza creada!');
        setInput({
            name : '',
            height_min: '',
            height_max: '',
            weight_min: '',
            weight_max: '',
            lifeSpan: '',
            image: '',
            temperament: []
        });
    }

    return(
        <>
            <Nav/>
            <hr  className = 'hr_add_add'/>
                <div className = 'div_add_one'>
                    <h1 className = 'title_add'>CREAR RAZA</h1>
                        <form onSubmit = {e => handleSubmit(e)} className = 'form_add'>
                            <div className = 'div_add_name'>
                                <input type="text" value = {input.name} name = 'name' onChange = {e => validateName(e.target.value)} placeholder = 'Nombre' className = 'input_add_name'/>
                                {!error ? null : <span><p className = 'name_error_add'>{err}</p></span>}
                            </div>
                            <div className = 'div_add_height'>
                                <input type="number" value = {input.height_max} name = 'height_max' className = 'input_add_height_metric' placeholder = 'Altura min cm'/>
                                <input type="number" value = {input.height_min} name="height_min" o className = 'input_add_height_imperial' placeholder = 'Altura max cm' />
                            </div>
                            <div className = 'div_add_weight'>
                                <input type="number" value = {input.weight_max} name = 'weight_max' className = 'input_add_weight_metric' placeholder = 'Peso min kg' />
                                <input type="number" value = {input.weight_min} name="weight_min"  className = 'input_add_weight_imperial' placeholder = 'Peso max kg' />
                                {err.weight && (
                                    <p className = 'weight_error_add'>{err.weight}</p>
                                )}
                            </div>
                            <div className = 'div_add_lifeSpan'>
                                <input type="number" value = {input.life_span} name="lifeSpan" className = 'input_add_lifeSpan' placeholder = 'Esperanza de vida' />
                                {err.lifeSpan && (
                                    <p className = 'lifeSpan_error_add'>{err.lifeSpan}</p>
                                )}
                            </div>
                            <div className = 'div_add_img'>
                                <input type="text" value = {input.image} name="image" onChange = {e => handleChange(e)} className = 'input_add_img' placeholder = 'URL Imagen' />
                            </div>
                            <div>
                                <select onChange = {e => handleSelect(e)} className = 'select_add_temps'>
                                    <option value="temperaments">Temperamentos</option>
                                    {temperaments.map(e => {
                                        return <option value={e.name}>{e.name}</option>
                                    })}
                                </select>
                                <ul className = 'ul_add'>
                                    {input.temperament.map(e => {
                                        return (
                                            <div className = 'div_add_temps'>
                                                <li className = 'li_add'>{e}</li> 
                                                <button className = 'button_delete_temp' onClick = {() => handleDelete(e)}>x</button> 
                                            </div>
                                        )
                                    })}
                                </ul>
                            </div>
                            <div>
                                <button className = 'buton_add_dog' type = 'submit'>Agregar </button>
                            </div>
                        </form>
            </div>
        </>
    )
}
