import { GET_DOGS, FILTER, GET_BY_NAME, ADD_DOG, GET_TEMPERAMENTS } from "../actions/constants";

let initialState = {
    dogs : [],
    allDogs : [],
};

const root = (state = initialState, action) => {
    switch (action.type) {
        case GET_DOGS :
        return {
            ...state,
            dogs: action.payload,
            allDogs : action.payload
        }
        case FILTER :
            const allDogs = state.allDogs;
            console.log(allDogs);
            const filter = action.payload == 'todos' ? allDogs : allDogs.filter(e => {
                if(action.payload === 'agregada'){
                    return e.status 
                }
                if(action.payload === 'existente'){
                    return !e.status
                }
            });
            return {
                ...state,
                dogs: filter
            };
        case GET_BY_NAME :  
            return {
                ... state,
                dogs: action.payload
            };
        case ADD_DOG :
            return {
                ... state
            }
        default :
        return state;
    }
};

export default root;
