import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

//import actions
import { addDog, getTemps } from '../../actions/actions';

//import components
import { Nav } from "../nav/nav";

//import css
import './add.css';

export const Add = () => {

    const [input, setInput] = useState({
        name : '',
        height_metric: '',
        height_imperial: '',
        weight_metric: '',
        weight_imperial: '',
        lifeSpan: '',
        image: '',
        temperaments: []
    });

    const dispatch = useDispatch();
    const temperaments = useSelector((state) => state.temperaments);
    useEffect(() => {
        dispatch(getTemps());
    }, []);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
    }

    return(
        <>
            <Nav/>
                <div className = 'div_add_one'>
                    <h1 className = 'title_add'>CREAR RAZA</h1>
                        <form className = 'form_add'>
                            <div>
                                <label> Nombre :</label>
                                <input type="text" value = {input.name} name = 'name' onChange = {handleChange} />
                            </div>
                            <div>
                                <label> Métrica de altura :</label>
                                <input type="number" value = {input.height_metric} name = 'heightMetric' onChange = {handleChange} />
                                <label> Altura imperial :</label>
                                <input type="number" value = {input.height_imperial} name="heightImperial" onChange = {handleChange} />
                            </div>
                            <div>
                                <label> Métrica de peso :</label>
                                <input type="number" value = {input.weight_metric} name = 'weightMetric' onChange = {handleChange} />
                                <label> Peso imperial :</label>
                                <input type="number" value = {input.weight_imperial} name="weighImperial" onChange = {handleChange} />
                            </div>
                            <div>
                                <label> Esperanza de vida :</label>
                                <input type="number" value = {input.life_span} name="lifeSpan" onChange = {handleChange} />
                            </div>
                            <div>
                                <label> Url imagen :</label>
                                <input type="text" value = {input.image} name="image" onChange = {handleChange} />
                            </div>
                            <div>
                                <select>
                                    {temperaments.map(e => {                                        return <option value={e.name}>{e.name}</option>
                                    })}
                                </select>
                            </div>
                            <div>
                                <button type = 'submit'>Agregar </button>
                            </div>
                        </form>
            </div>
        </>
    )
}
