import { GET_DOGS, FILTER } from "../actions/constants";

let initialState = {
    dogs : [],
};

const root = (state = initialState, action) => {
    switch (action.type) {
        case GET_DOGS : 
        return {
            ...state,
            dogs: action.payload
        }
        case FILTER :
            const allDogs = state.dogs;
            const filter = []
            if(action.payload === 'agregada'){
                    allDogs.map(e => {
                        filter.push(e.name === 'Affenpinscher')
                    })
            }
            return {
                ...state,
                dogs: filter
            }
        default : 
        return state;
    }
};

export default root;
