import { GET_DOGS, FILTER } from "../actions/constants";

let initialState = {
    dogs : [],
};

const root = (state = initialState, action) => {
    switch (action.type) {
        case GET_DOGS :
        return {
            ...state,
            dogs: action.payload
        }
        case FILTER :
            const allDogs = state.dogs;
            let filter = [];
            if(action.payload == 'todos'){
                console.log('entreeee', allDogs);
                filter = state.dogs;
                return filter
            }
            if(action.payload = 'existente'){
                filter = allDogs.filter( e => e.name === 'Affenpinscher')
            }
            // let filter = action.payload === 'todos'? allDogs : allDogs.filter(e => e.name === 'Affenpinscher')
            return {
                ...state,
                dogs: filter
            }
        default :
        return state;
    }
};

export default root;
