import { GET_DOGS, FILTER } from "../actions/constants";

let initialState = {
    dogs : [],
};

const root = (state = initialState, action) => {
    switch (action.type) {
        case GET_DOGS : 
        return {
            ...state,
            dogs: action.payload
        }
        case FILTER :
            const todos = state.dogs;
            console.log('soy action'.action.payload);
            console.log('soy todos',todos);
            const filter = action.payload === 'Todos' ? todos : todos.filter( e => e.status === action.payload)
            return {
                ...state,
                dogs: filter

            }
        default : 
        return state;
    }
};

export default root;
