import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

//import actions
import { addDog, getTemps } from '../../actions/actions';

//import components
import { Nav } from "../nav/nav";

//import css
import './add.css';

export const Add = () => {

    const [input, setInput] = useState({
        name : '',
        height_metric: '',
        height_imperial: '',
        weight_metric: '',
        weight_imperial: '',
        lifeSpan: '',
        image: '',
        temperament: []
    });

    const dispatch = useDispatch();
    const temperaments = useSelector((state) => state.temperaments);
    useEffect(() => {
        dispatch(getTemps());
    }, [dispatch]);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
    };

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperament: [...input.temperament, e.target.value]
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(addDog(input));
        alert('Raza creada!');
        setInput({
            name : '',
            height_min: '',
            height_max: '',
            weight_min: '',
            weight_max: '',
            lifeSpan: '',
            image: '',
            temperament: []
        });
    }

    return(
        <>
            <Nav/>
                <div className = 'div_add_one'>
                    <h1 className = 'title_add'>CREAR RAZA</h1>
                        <form onSubmit = {e => handleSubmit(e)} className = 'form_add'>
                            <div className = 'div_add_name'>
                                <input type="text" value = {input.name} name = 'name' onChange = {e => handleChange(e)} placeholder = 'Nombre' className = 'input_add_name'/>
                            </div>
                            <div className = 'div_add_height'>
                                <input type="number" value = {input.height_max} name = 'height_max' onChange = {e => handleChange(e)} className = 'input_add_height_metric' placeholder = 'Altura min'/>
                                <input type="number" value = {input.height_min} name="height_min" onChange = {e => handleChange(e)} className = 'input_add_height_imperial' placeholder = 'Altura max' />
                            </div>
                            <div className = 'div_add_weight'>
                                <input type="number" value = {input.weight_max} name = 'weight_max' onChange = {e => handleChange(e)} className = 'input_add_weight_metric' placeholder = 'Peso min' />
                                <input type="number" value = {input.weight_min} name="weight_min" onChange = {e => handleChange(e)} className = 'input_add_weight_imperial' placeholder = 'Peso max' />
                            </div>
                            <div className = 'div_add_lifeSpan'>
                                <input type="number" value = {input.life_span} name="lifeSpan" onChange = {e => handleChange(e)} className = 'input_add_lifeSpan' placeholder = 'Esperanza de vida' />
                            </div>
                            <div className = 'div_add_img'>
                                <input type="text" value = {input.image} name="image" onChange = {e => handleChange(e)} className = 'input_add_img' placeholder = 'URL Imagen' />
                            </div>
                            <div>
                                <select onChange = {e => handleSelect(e)} className = 'select_add_temps'>
                                    <option value="temperaments">Temperamentos</option>
                                    {temperaments.map(e => {
                                        return <option value={e.name}>{e.name}</option>
                                    })}
                                </select>
                                <ul className = 'ul_add'>
                                    {input.temperament.map(e => {
                                        return <li className = 'li_add'>{e}</li>
                                    })}
                                </ul>
                            </div>
                            <div>
                                <button className = 'buton_add_dog' type = 'submit'>Agregar </button>
                            </div>
                        </form>
            </div>
        </>
    )
}
